// src/pages/Profile.jsx
import React, { useState, useEffect } from 'react';
import api from '../api';
import { useNavigate } from 'react-router-dom';
import { useContext } from 'react';
import { AuthContext } from '../context/AuthContext';

const Profile = () => {
  const [profile, setProfile] = useState({
    name: '',
    age: '',
    gender: '',
    health_conditions: '',
    birthmarks: '',
    family_medication_history: '',
    previous_medication_history: ''
  });
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(true);

  // NEW STATE: Control when to show the puzzles
  const [showPuzzles, setShowPuzzles] = useState(false);

  // PUZZLE STATES
  const [captchaAnswer, setCaptchaAnswer] = useState('');
  const [moodSelection, setMoodSelection] = useState(null);

  const navigate = useNavigate();
  const { logout } = useContext(AuthContext);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await api.get('/api/user/profile');
        setProfile(response.data || {});
      } catch (error) {
        setMessage(error.friendlyMessage || 'Failed to load profile');
      } finally {
        setLoading(false);
      }
    };
    fetchProfile();
  }, []);

  const handleChange = (e) => {
    setProfile({ ...profile, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setShowPuzzles(false); // Hide puzzles while submitting
    try {
      await api.put('/api/user/profile', profile);
      setMessage('Profile updated successfully! Please complete the quick check below.');
      setShowPuzzles(true); // SHOW PUZZLES ON SUCCESS
    } catch (error) {
      setMessage(error.friendlyMessage || 'Failed to update profile');
      setShowPuzzles(false);
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  // PUZZLE LOGIC
  const handleSubmitCaptcha = (e) => {
    e.preventDefault();
    const correctSum = 15; 
    if (parseInt(captchaAnswer, 10) === correctSum) {
      alert('Success! You passed the attention check.');
    } else {
      alert('Incorrect. Please try again.');
    }
    setCaptchaAnswer('');
  };

  const handleMoodSelect = (mood) => {
    setMoodSelection(mood);
    alert(`Thank you for sharing. Mood selected: ${mood}. This information is being used for your mental well-being check.`);
  };

  // Puzzle Content JSX
  const PuzzleSection = () => (
    <div className="mt-8 pt-6 border-t border-gray-200">
        <h2 className="text-2xl font-bold mb-4">Cognitive and Well-being Puzzles</h2>
        <p className="text-md mb-6 text-gray-700">
            These checks help us ensure the reliability of your health assessment.
        </p>

        {/* Section 1: Attention Check (Human/Bot Puzzle) */}
        <section className="mb-6 border p-4 rounded">
            <h3 className="text-xl font-semibold mb-3">Attention Check: Prove You are Human</h3>
            <p className="mb-3">
            To ensure reliable well-being assessment, please solve the puzzle:
            <strong className="block text-lg mt-1">What is the sum of 7 and 8?</strong>
            </p>
            <form onSubmit={handleSubmitCaptcha} className="flex gap-2">
            <input
                type="number"
                value={captchaAnswer}
                onChange={(e) => setCaptchaAnswer(e.target.value)}
                className="p-2 border rounded w-32"
                placeholder="Enter sum"
                required
            />
            <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        </section>

        {/* Section 2: Simple Mental State Assessment */}
        <section className="mb-6 border p-4 rounded bg-gray-100">
            <h3 className="text-xl font-semibold mb-3">Mental State Check: How are you feeling right now?</h3>
            <p className="mb-4 text-gray-700">Select the mood that best describes your current mental condition:</p>
            <div className="flex flex-wrap gap-4">
            {['Calm', 'Stressed', 'Anxious', 'Energetic', 'Fatigued', 'Neutral'].map((mood) => (
                <button
                key={mood}
                onClick={() => handleMoodSelect(mood)}
                className={`px-4 py-2 rounded shadow transition-colors ${
                    moodSelection === mood
                    ? 'bg-blue-600 text-white font-bold'
                    : 'bg-white hover:bg-gray-200 text-gray-800'
                }`}
                >
                {mood}
                </button>
            ))}
            </div>
        </section>
    </div>
  );


  return (
    <div className="max-w-md mx-auto bg-white p-6 rounded shadow">
      <h2 className="text-2xl font-bold mb-4">Profile</h2>
  {loading ? <p>Loading...</p> : <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label className="block text-gray-700">Name</label>
          <input
            type="text"
            name="name"
            value={profile.name}
            onChange={handleChange}
            className="w-full p-2 border rounded"
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">Age</label>
          <input
            type="number"
            name="age"
            value={profile.age}
            onChange={handleChange}
            className="w-full p-2 border rounded"
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">Gender</label>
          <input
            type="text"
            name="gender"
            value={profile.gender}
            onChange={handleChange}
            className="w-full p-2 border rounded"
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">Health Conditions</label>
          <textarea
            name="health_conditions"
            value={profile.health_conditions}
            onChange={handleChange}
            className="w-full p-2 border rounded"
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">Birthmarks</label>
          <textarea
            name="birthmarks"
            value={profile.birthmarks}
            onChange={handleChange}
            className="w-full p-2 border rounded"
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700">Family Medication History</label>
          <textarea
            name="family_medication_history"
            value={profile.family_medication_history}
            onChange={handleChange}
            className="w-full p-2 border rounded"
            placeholder="e.g., Medications taken by family members for chronic conditions"
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">Previous Medication History</label>
          <textarea
            name="previous_medication_history"
            value={profile.previous_medication_history}
            onChange={handleChange}
            className="w-full p-2 border rounded"
            placeholder="e.g., Any medications you have taken in the past year, including dosage and reason"
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700">AI Response Style</label>
          <select name="response_style" value={profile.response_style || 'concise'} onChange={handleChange} className="w-full p-2 border rounded">
            <option value="concise">Concise</option>
            <option value="detailed">Detailed</option>
          </select>
        </div>
  <button type="submit" className="w-full btn btn-primary">Update Profile</button>
      </form>}
      <div className="mt-4">
  <button onClick={handleLogout} className="w-full btn btn-secondary">Logout</button>
      </div>
      {message && <p className="mt-4 text-center">{message}</p>}

      {/* CONDITIONAL DISPLAY: Show puzzles if not loading and update was successful */}
      {!loading && showPuzzles && <PuzzleSection />}

    </div>
  );
};

export default Profile;
