import React, { useState, useRef, useEffect } from 'react';
import api from '../api';

const Chat = () => {
  const [message, setMessage] = useState('');
  const [chatHistory, setChatHistory] = useState([]);
  const [isSending, setIsSending] = useState(false);

  const getSentimentStyle = (label) => {
    switch (label.toLowerCase()) {
      case 'positive':
        return { color: 'green', fontWeight: 'bold' };
      case 'negative':
        return { color: 'red', fontWeight: 'bold' };
      case 'neutral':
        return { color: 'orange', fontWeight: 'bold' };
      default:
        return {};
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!message.trim()) return;
    setIsSending(true);

    try {
      const response = await api.post('/api/chat/', { message });
      const { ai_response, sentiment } = response.data;
      setChatHistory((h) => [...h, { user: message, ai: ai_response, sentiment }]);
      setMessage('');
    } catch (error) {
      console.error(error);
      alert(error.response?.data?.message || 'Failed to send message');
    }
    setIsSending(false);
  };

  const messagesRef = useRef(null);
  useEffect(() => {
    if (messagesRef.current) messagesRef.current.scrollTop = messagesRef.current.scrollHeight;
  }, [chatHistory]);

  return (
    <div className="max-w-2xl mx-auto bg-white p-6 rounded shadow">
      <h2 className="text-2xl font-bold mb-4">Chat with AI Assistant</h2>
      <div ref={messagesRef} className="mb-4 h-64 overflow-y-auto border p-4">
        {chatHistory.map((chat, index) => (
          <div key={index} className="mb-2">
            <p><strong>You:</strong> {chat.user}</p>
            <p><strong>AI:</strong> {chat.ai}</p>
            {chat.sentiment && (
              <p>
                <strong>Sentiment:</strong> <span style={getSentimentStyle(chat.sentiment.label)}>{chat.sentiment.label}</span> ({Math.round((chat.sentiment.score || 0) * 100)}%)
              </p>
            )}
          </div>
        ))}
      </div>
      <form onSubmit={handleSubmit}>
        <div className="flex">
          <input
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            className="flex-1 p-2 border rounded-l"
            placeholder="Type your message..."
          />
          <button type="submit" disabled={isSending} className="btn btn-primary rounded-r">{isSending ? 'Sending...' : 'Send'}</button>
        </div>
      </form>
    </div>
  );
};

export default Chat;
