// src/pages/Profile.jsx
import React, { useState, useEffect } from 'react';
import api from '../api';
import { useNavigate } from 'react-router-dom';
import { useContext } from 'react';
import { AuthContext } from '../context/AuthContext';

const Profile = () => {
  const [profile, setProfile] = useState({
    name: '',
    age: '',
    gender: '',
    health_conditions: '',
    birthmarks: '',
    family_medication_history: '',
    previous_medication_history: ''
  });
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const { logout } = useContext(AuthContext);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await api.get('/api/user/profile');
        setProfile(response.data || {});
      } catch (error) {
        setMessage(error.friendlyMessage || 'Failed to load profile');
      } finally {
        setLoading(false);
      }
    };
    fetchProfile();
  }, []);

  const handleChange = (e) => {
    setProfile({ ...profile, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.put('/api/user/profile', profile);
      setMessage('Profile updated successfully!');
    } catch (error) {
      setMessage(error.friendlyMessage || 'Failed to update profile');
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <div className="max-w-md mx-auto bg-white p-6 rounded shadow">
      <h2 className="text-2xl font-bold mb-4">Profile</h2>
  {loading ? <p>Loading...</p> : <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label className="block text-gray-700">Name</label>
          <input
            type="text"
            name="name"
            value={profile.name}
            onChange={handleChange}
            className="w-full p-2 border rounded"
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">Age</label>
          <input
            type="number"
            name="age"
            value={profile.age}
            onChange={handleChange}
            className="w-full p-2 border rounded"
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">Gender</label>
          <input
            type="text"
            name="gender"
            value={profile.gender}
            onChange={handleChange}
            className="w-full p-2 border rounded"
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">Health Conditions</label>
          <textarea
            name="health_conditions"
            value={profile.health_conditions}
            onChange={handleChange}
            className="w-full p-2 border rounded"
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">Birthmarks</label>
          <textarea
            name="birthmarks"
            value={profile.birthmarks}
            onChange={handleChange}
            className="w-full p-2 border rounded"
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700">Family Medication History</label>
          <textarea
            name="family_medication_history"
            value={profile.family_medication_history}
            onChange={handleChange}
            className="w-full p-2 border rounded"
            placeholder="e.g., Medications taken by family members for chronic conditions"
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">Previous Medication History</label>
          <textarea
            name="previous_medication_history"
            value={profile.previous_medication_history}
            onChange={handleChange}
            className="w-full p-2 border rounded"
            placeholder="e.g., Any medications you have taken in the past year, including dosage and reason"
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700">AI Response Style</label>
          <select name="response_style" value={profile.response_style || 'concise'} onChange={handleChange} className="w-full p-2 border rounded">
            <option value="concise">Concise</option>
            <option value="detailed">Detailed</option>
          </select>
        </div>
  <button type="submit" className="w-full btn btn-primary">Update Profile</button>
      </form>}
      <div className="mt-4">
  <button onClick={handleLogout} className="w-full btn btn-secondary">Logout</button>
      </div>
      {message && <p className="mt-4 text-center">{message}</p>}
    </div>
  );
};

export default Profile;
